import { Axios } from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const BoardContent = () => {
  const { num } = useParams();
  const [boardContentData, setBoardContentData] = useState({});

  useEffect(() => {
    Axios.get(`http://localhost:8000/api/select/${num}`).then((res) => {
      setBoardContentData(res.data);
    });
  }, []);
  return (
    <div>
      <table border="1px solid">
        <tr>
          <td>게시물 번호</td>
        </tr>
        <tr>
          <td>{boardContentData.num}</td>
        </tr>
        <tr>
          <td>제목</td>
        </tr>
        <tr>
          <td>{boardContentData.title}</td>
        </tr>
        <tr>
          <td>내용</td>
        </tr>
        <tr>
          <td>{boardContentData.content}</td>
        </tr>
        <tr>
          s<td>작성자</td>
        </tr>
        <tr>
          <td>{boardContentData.id}</td>
        </tr>
      </table>
      <button>
        <a href="/"></a>
      </button>
    </div>
  );
};

export default BoardContent;
