import "./App.css";
import Axios from "axios";
import { useState } from "react";
import { useRef } from "react";
function App() {
  const submitTest = () => {
    Axios.get("http://localhost:4004/", {}).then(() => {
      alert("등록 완료!");
    });
  };

  const onSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:4004/board", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: titleRef.current.value,
        content: contentRef.current.value,
        email: emailRef.current.value,
      }),
    }).then((res) => {
      if (res.ok) {
        alert("생성이 완료 되었습니다.");
      }
    });
  };
  const titleRef = useRef(null);
  const contentRef = useRef(null);
  const emailRef = useRef(null);
  return (
    <div>
      <form
      // method="post"
      // action="http://localhost:4004/board"
      // enctype="multipart/form-data"
      >
        <input type="text" name="title" placeholder="제목" ref={titleRef} />
        <input type="text" name="content" placeholder="내용" ref={contentRef} />
        <input type="email" name="email" placeholder="작성자" ref={emailRef} />
        {/* <button type="submit">저장</button> */}
      </form>
      {/* <button onClick={submitTest}>업로드</button> */}
      <button onClick={onSubmit}>업로드</button>
    </div>
  );
}

export default App;
